00047         static const unsigned int CANARY_LEN = 64;
00048     #else
00049         static const unsigned int CANARY_LEN = 0;
00050     #endif
00051     static const unsigned char canary_byte1 = 'k';
00052     static const unsigned char canary_byte2 = 'K';
00053     static unsigned char random_byte;
00054 
00055     typedef enum {FALSE,TRUE} BOOL;
