00060     static const char * basename(const char * s) {
00061         const char *s1,*s2;
00062         s1 = strrchr(s,'/');
00063         if(s1==NULL) s1 = s; else s1++;
00064         s2 = strrchr(s1, '\\');
00065         if(s2==NULL) s2 = s1; else s2++;
00066         return s2;
00067     }
00068 
00069     static char *StrCpy(char ** to, const char * from) {
00070             if(from == NULL) {
00071             *to = NULL;
00072             } else {
00073                     *to = (char*)malloc(strlen(from)+1);
00074                     if(*to) strcpy(*to, from);
00075             }
00076             return *to;
00077     }
00078 
00079     static void *canary_malloc(size_t size, unsigned char data) {
00080         char *p = (char *)malloc(size+2*CANARY_LEN);
00081         if (p) {
00082             memset(p, canary_byte1, CANARY_LEN);
00083             memset(p+CANARY_LEN, data, size);
00084             memset(p+CANARY_LEN+size, canary_byte2, CANARY_LEN);
00085         }
00086         return p;
00087     }
00088 
00089     static int chk_canary(void *p, size_t size) {
00090         unsigned char *pc = (unsigned char*)p;
00091         unsigned int i;
00092         for (i = 0; i < CANARY_LEN; i++)
00093             if (pc[i] != canary_byte1)
00094                 return -1;
00095         pc += CANARY_LEN+size;
00096         for (i = 0; i < CANARY_LEN; i++)
00097             if (pc[i] != canary_byte2)
00098                 return 1;
00099         return 0;
00100     }
00101 
00102     typedef struct {
00103         int f;  /* allocator func */
00104         int line;
00105         char * par_txt;
00106         char * file;
00107     } call_t;
00108 
00109     static call_t pack(int f, const char * par_txt, int line, const char * file) {
00110         call_t ret;
00111         ret.f = f;
00112         ret.line = line;
00113         StrCpy(&ret.par_txt, par_txt);
00114         StrCpy(&ret.file, file);
00115         return ret;
00116     }
00117 
00118     static void print_call(const char * msg, call_t call) {
00119         if(msg) fprintf(fperror, "%s", msg);
00120         fprintf(fperror, "%s", pretty[call.f]);
00121         fprintf(fperror, "%s", call.par_txt ? call.par_txt : "?");
00122         if (call.f <= 3) fprintf(fperror, ")");
00123         fprintf(fperror," @ %s:", call.file ? basename(call.file) : "?");
00124         fprintf(fperror,"%d\n",call.line ? call.line : 0);
00125     }
