00127     /* memoriateruletet dump */
00128     static void dump_memory(void const *mem, size_t size, size_t can_len, FILE* fp) {
00129         unsigned char const *m=(unsigned char const *) mem;
00130         unsigned int s, o;
00131 
00132         if (can_len > 0)
00133             fprintf(fp, "Dump (addr: %p kanari hossz: %d):\n", m+can_len, (int)can_len);
00134         else
00135             fprintf(fp, "Dump: (addr: %p) \n", m);
00136         size += 2*can_len;
00137         for (s = 0; s < (size+15)/16; s++) {
00138             fprintf(fp, "%04x:%c ", s*16, s*16 < can_len || s*16 >= size-can_len ? ' ' : '*');
00139             for (o = 0; o < 16; o++) {
00140                 if (o == 8) fprintf(fp, " ");
00141                 if (s*16+o < size)
00142                     fprintf(fp, "%02x ", m[s*16+o]);
00143                 else
00144                     fprintf(fp, "   ");
00145             }
00146             fprintf(fp, " ");
00147             for (o = 0; o < 16; o++) {
00148                 if (s*16+o < size)
00149                     fprintf(fp, "%c", isprint(m[s*16+o]) ? m[s*16+o] : '.');
00150                 else
00151                     fprintf(fp, " ");
00152             }
00153             fprintf(fp, "\n");
00154         }
00155     }
00156 
00157     void mem_dump(void const *mem, size_t size, FILE* fp) {
00158         dump_memory(mem, size, 0, fp);
00159     }
