00163     static void die(const char * msg, void * p, size_t size, call_t * a, call_t * d) {
00164         #ifdef MEMTRACE_ERRFILE
00165             fperror = fopen(XSTR(MEMTRACE_ERRFILE), "w");
00166         #endif
00167         fprintf(fperror,"%s\n",msg);
00168         if (p) {
00169             fprintf(fperror, "\tPointer:\t%p", PU(p));
00170             if (size) fprintf(fperror," (%d byte)", (int)size);
00171             fprintf(fperror,"\n");
00172         }
00173         if (a) print_call("\tFoglalas:\t", *a);
00174         if (d) print_call("\tFelszabaditas:\t", *d);
00175                 if (p) dump_memory(p, size, CANARY_LEN, fperror);
00176 
00177         dying = TRUE;
00178         exit(120);
00179     }
