00190     typedef struct _registry_item {
00191         void * p;    /* mem pointer*/
00192         size_t size; /* size*/
00193         call_t call;
00194         struct _registry_item * next;
00195     } registry_item;
00196 
00197     static registry_item registry; /*sentinel*/
00198 
00199     static void print_registry_item(registry_item * p) {
00200         if (p) {
00201             print_registry_item(p->next);
00202             fprintf(fperror, "\t%p%5d byte ",p->p, (int)p->size);
00203             print_call(NULL, p->call);
00204             if(p->call.par_txt) free(p->call.par_txt);
00205             if(p->call.file) free(p->call.file);
00206             free(p);
00207         }
00208     }
