00210     /* ha nincs hiba, akkor 0-val tér vissza */
00211     int mem_check(void) {
00212         initialize();
00213         if(dying) return  2;    /* címzési hiba */
00214 
00215         if(registry.next) {
00216             /*szivarog*/
00217             #ifdef MEMTRACE_ERRFILE
00218                 fperror = fopen(XSTR(MEMTRACE_ERRFILE), "w");
00219             #endif
00220             fprintf(fperror, "Szivargas:\n");
00221             print_registry_item(registry.next);
00222             registry.next = NULL;
00223             return 1;           /* memória fogyás */
00224         }
00225         return 0;
00226     }
