00245     static int allocated_blks;
00246 
00247     int allocated_blocks() { return allocated_blks; }
00248 
00249     static BOOL register_memory(void * p, size_t size, call_t call) {
00250         initialize();
00251         allocated_blks++;
00252         #ifdef MEMTRACE_TO_FILE
00253             fprintf(trace_file, "%p\t%d\t%s%s", PU(p), (int)size, pretty[call.f], call.par_txt ? call.par_txt : "?");
00254             if (call.f <= 3) fprintf(trace_file, ")");
00255             fprintf(trace_file, "\t%d\t%s\n", call.line, call.file ? call.file : "?");
00256             fflush(trace_file);
00257         #endif
00258         #ifdef MEMTRACE_TO_MEMORY
00259         {/*C-blokk*/
00260             registry_item * n = (registry_item*)malloc(sizeof(registry_item));
00261             if(n==NULL) return FALSE;
00262             n->p = p;
00263             n->size = size;
00264             n->call = call;
00265             n->next = registry.next;
00266             registry.next = n;
00267         }/*C-blokk*/
00268         #endif
00269 
00270         return TRUE;
00271     }
00272 
00273     #ifdef MEMTRACE_TO_MEMORY
00274     static registry_item *find_registry_item(void * p) {
00275                 registry_item *n = &registry;
00276                 for(; n->next && n->next->p != p ; n=n->next);
00277                 return n;
00278         }
00279         #endif
00280 
00281     static void unregister_memory(void * p, call_t call) {
00282         initialize();
00283         #ifdef MEMTRACE_TO_FILE
00284                         fprintf(trace_file, "%p\t%d\t%s%s", PU(p), -1, pretty[call.f], call.par_txt ? call.par_txt : "?");
00285                         if (call.f <= 3) fprintf(trace_file, ")");
00286             fprintf(trace_file,"\t%d\t%s\n",call.line, call.file ? call.file : "?");
00287             fflush(trace_file);
00288         #endif
00289         #ifdef MEMTRACE_TO_MEMORY
00290         { /*C-blokk*/
00291             registry_item * n = find_registry_item(p);
00292             if(n->next) {
00293                 allocated_blks--;
00294                 registry_item * r = n->next;
00295                 n->next = r->next;
00296                 if(COMP(r->call.f,call.f)) {
00297                     int chk = chk_canary(r->p, r->size);
00298                     if (chk < 0)
00299                         die("Blokk elott serult a memoria:", r->p,r->size,&r->call,&call);
00300                     if (chk > 0)
00301                         die("Blokk utan serult a memoria", r->p,r->size,&r->call,&call);
00302                     /*rendben van minden*/
00303                     if(call.par_txt) free(call.par_txt);
00304                     if(r->call.par_txt) free(r->call.par_txt);
00305                     if(call.file) free(call.file);
00306                     if(r->call.file) free(r->call.file);
00307                     memset(PU(r->p), 'f', r->size);
00308                     PU(r->p)[r->size-1] = 0;
00309                     free(r);
00310                 } else {
00311                     /*hibas felszabaditas*/
00312                     die("Hibas felszabaditas:",r->p,r->size,&r->call,&call);
00313                 }
00314             } else {
00315                 die("Nem letezo, vagy mar felszabaditott adat felszabaditasa:", p, 0,NULL,&call);
00316             }
00317         } /*C-blokk*/
00318         #endif
00319     }
00320 END_NAMESPACE
