00326 #ifdef MEMTRACE_C
00327 START_NAMESPACE
00328     void * traced_malloc(size_t size, const char * par_txt, int line, const char * file) {
00329         void * p;
00330         initialize();
00331         p = canary_malloc(size, random_byte);
00332         if (p) {
00333             if(!register_memory(p,size,pack(FMALLOC,par_txt,line,file))) {
00334                 free(p);
00335                 return NULL;
00336             }
00337             return PU(p);
00338         }
00339         return NULL;
00340     }
00341 
00342     void * traced_calloc(size_t count, size_t size, const char * par_txt, int line, const char * file) {
00343         void * p;
00344         initialize();
00345                 size *= count;
00346                 p = canary_malloc(size, 0);
00347         if(p) {
00348             if(!register_memory(p,size,pack(FCALLOC,par_txt,line,file))) {
00349                 free(p);
00350                 return NULL;
00351             }
00352             return PU(p);
00353         }
00354         return NULL;
00355     }
00356 
00357     void traced_free(void * pu, const char * par_txt, int line, const char * file) {
00358         initialize();
00359         if(pu) {
00360             unregister_memory(P(pu), pack(FFREE,par_txt,line,file));
00361             free(P(pu));
00362         } else {
00363             /*free(NULL) eset*/
00364             #ifdef MEMTRACE_TO_FILE
00365                 fprintf(trace_file,"%s\t%d\t%10s\t","NULL",-1,pretty[FFREE]);
00366                 fprintf(trace_file,"%d\t%s\n",line,file ? file : "?");
00367                 fflush(trace_file);
00368             #endif
00369             #ifndef ALLOW_FREE_NULL
00370             {/*C-blokk*/
00371                 call_t call;
00372                 call = pack(FFREE,par_txt,line,file);
00373                 die("free(NULL) hivasa:",NULL,0,NULL,&call);
00374             }/*C-blokk*/
00375             #endif
00376         }
00377     }
00378 
00379     void * traced_realloc(void * old, size_t size, const char * par_txt, int line, const char * file) {
00380         void * p;
00381         size_t oldsize = 0;
00382         registry_item * n;
00383         initialize();
00384 
00385         #ifdef MEMTRACE_TO_MEMORY
00386                 n = find_registry_item(P(old));
00387                 if (n) oldsize = n->next->size;
00388             p = canary_malloc(size, random_byte);
00389             #else
00390                 p = realloc(old, size);
00391             #endif
00392         if (p) {
00393             /*Ha sikerult a foglalas, regisztraljuk*/
00394             register_memory(p,size,pack(FREALLOC, par_txt, line,file));
00395                     if (old) {
00396                 #ifdef MEMTRACE_TO_MEMORY
00397                     int cpsize = 2*CANARY_LEN;
00398                             if (oldsize < size) cpsize += oldsize;
00399                             else cpsize += size;
00400                             memcpy(p, P(old), cpsize);
00401                 #endif
00402                         unregister_memory(P(old), pack(FREALLOC, par_txt, line, file));
00403                 #ifdef MEMTRACE_TO_MEMORY
00404                             free P(old);
00405                         #endif
00406                     }
00407                     return PU(p);
00408         } else {
00409                 return NULL;
00410             }
00411     }
