00438     void * traced_new(size_t size, int line, const char * file, int func) {
00439         initialize();
00440         for (;;) {
00441             void * p = canary_malloc(size, random_byte);
00442             if(p) {
00443                 register_memory(p,size,pack(func,"",line,file));
00444                 return PU(p);
00445             }
00446 
00447             if (_new_handler == 0)
00448                 throw std::bad_alloc();
00449 
00450             _new_handler();
00451         }
00452     }
