00454     void traced_delete(void * pu, int func) {
00455         initialize();
00456         if(pu) {
00457             /*kiolvasom call-t, ha van*/
00458             memtrace::call_t call = delete_called ? (delete_call.f=func, delete_call) : pack(func,NULL,0,NULL);
00459             memtrace::unregister_memory(P(pu),call);
00460             free(P(pu));
00461         }
00462         delete_called=FALSE;
00463     }
