00514 START_NAMESPACE
00515     static void initialize() {
00516         static BOOL first = TRUE;
00517         if(first) {
00518             fperror = stderr;
00519             random_byte = (unsigned char)time(NULL);
00520             first = FALSE;
00521             dying = FALSE;
00522             #ifdef MEMTRACE_TO_MEMORY
00523                 registry.next = NULL;
00524                 #if !defined(USE_ATEXIT_OBJECT) && defined(MEMTRACE_AUTO)
00525                     atexit((void(*)(void))mem_check);
00526                 #endif
00527             #endif
00528             #ifdef MEMTRACE_TO_FILE
00529                 trace_file = fopen("memtrace.dump","w");
00530             #endif
00531             #ifdef MEMTRACE_CPP
00532                 _new_handler = NULL;
00533                 delete_called = FALSE;
00534                 delete_call = pack(0,NULL,0,NULL);
00535             #endif
00536         }
00537     }
